        private async void OnDifficultyChanged(object sender, EventArgs e)
        {
            if (DifficultyPicker.SelectedIndex == -1) return;

            _currentLevelIndex = DifficultyPicker.SelectedIndex;
            _correctWordsOnCurrentLevel = 0;
            _streak = 0;
            _usedWords.Clear();
            UpdateTheme();
            UpdateDisplay();
            SaveState(); // Save the new level selection
            await TriggerGlitchEffect();
        }

        private async Task TriggerGlitchEffect()
        {
            if (_isGlitchEffectActive) return;

            _isGlitchEffectActive = true;
            var originalColor = MainGrid.BackgroundColor;

            for (int i = 0; i < 3; i++)
            {
                // FIX: Correctly reference MainGrid as the actual type instead of 'object'.  
                MainGrid.BackgroundColor = _glitchColors[new Random().Next(_glitchColors.Length)];
                await Task.Delay(50);
            }

            MainGrid.BackgroundColor = originalColor;
            _isGlitchEffectActive = false;
        }

        // ✅ FIX & RENAME: This is the main button to get a new word.
        private void OnGenerateWordClicked(object sender, EventArgs e)
        {
            // Example word list
            var currentLevel = _levels[_currentLevelIndex];
            var wordList = _wordLists[currentLevel];
            _currentWord = wordList[_random.Next(wordList.Count)];


            _currentAttempt = "";
            CurrentAttemptLabel.Text = "";

            // Speak it
            TextToSpeech.SpeakAsync(_currentWord);

            // Shuffle letters
            var shuffled = _currentWord.ToCharArray().OrderBy(x => _random.Next()).ToArray();

            // Clear previous buttons
            LetterButtonsLayout.Children.Clear();

            foreach (char c in shuffled)
            {
                var button = new Button
                {
                    Text = c.ToString().ToUpper(),
                    WidthRequest = 50,
                    HeightRequest = 50,
                    BackgroundColor = Colors.DarkSlateBlue,
                    TextColor = Colors.White
                };

                button.Clicked += (s, args) =>
                {
                    _currentAttempt += c;
                    CurrentAttemptLabel.Text = _currentAttempt;

                    if (_currentAttempt.Length == _currentWord.Length)
                        CheckAttempt();
                };

                LetterButtonsLayout.Children.Add(button);
            }
        }


