        private static async Task<string> GetMistakeAnalysisAsync(string userWord, string correctWord)
        {
            if (string.IsNullOrWhiteSpace(userWord))
                return "You didn't enter anything. Try again!";

            try
            {
                var httpClient = new HttpClient();
               
                var prompt = $"The correct spelling is '{correctWord}', but the user wrote '{userWord}'. " +
                             "In one short sentence, explain the most likely mistake in a fun, glitchy style.";

                var requestBody = new
                {
                    model = "gpt-3.5-turbo",
                    messages = new[]
                    {
                       new { role = "system", content = "You are a helpful spelling coach for a futuristic game called OrthoGlitch. Keep responses under 15 words." },
                       new { role = "user", content = prompt }
                   },
                    max_tokens = 50
                };

                var response = await httpClient.PostAsync(
                    "https://api.openai.com/v1/chat/completions",
                    new StringContent(JsonSerializer.Serialize(requestBody), Encoding.UTF8, "application/json"));

                if (response.IsSuccessStatusCode)
                {
                    var responseContent = await response.Content.ReadAsStringAsync();
                    var result = JsonSerializer.Deserialize<OpenAIResponse>(responseContent);
                    return result?.Choices?[0]?.Message?.Content ?? "AI couldn't analyze the mistake. Try again!";
                }

                return "Keep practicing!";
            }
            catch (Exception ex)
            {
                return $"SYSTEM GLITCH: {ex.Message}";
            }
        }

        // Helper class for OpenAI response
        private class OpenAIResponse
        {
            public Choice[]? Choices { get; set; }
            public class Choice
            {
                public Message? Message { get; set; }
            }
            public class Message
            {
                public string? Content { get; set; }
            }
        }
        private async Task SpeakCurrentWord()
        {
            try
            {
                var locales = await TextToSpeech.GetLocalesAsync();
                var englishUS = locales.FirstOrDefault(l => l.Language == "en" && l.Country == "US");

                await TextToSpeech.SpeakAsync(_currentWord, new SpeechOptions
                {
                    Pitch = _currentLevelIndex switch { 0 => 1.0f, 1 => 1.2f, _ => 0.8f },
                    Volume = 1.0f,
                    Locale = englishUS
                });
            }
            catch (Exception ex)
            {
                await DisplayAlert("SYSTEM ERROR", $"AUDIO MODULE FAILURE: {ex.Message}", "ACKNOWLEDGE");
            }
        }
        private void GenerateLetterButtons(string word)
        {
            LetterButtonsLayout.Children.Clear();
            var random = new Random();
            var letters = word.ToCharArray().OrderBy(x => random.Next()).ToList();

            foreach (var letter in letters)
            {
                var btn = new Button
                {
                    Text = letter.ToString(),
                    WidthRequest = 50,
                    HeightRequest = 50,
                    CornerRadius = 25,
                    FontSize = 24,
                    BackgroundColor = Colors.White,
                    TextColor = Colors.Black,
                    Margin = new Thickness(5)
                };
                btn.Clicked += OnLetterButtonClicked;
                LetterButtonsLayout.Children.Add(btn);
            }
        }

        private void OnLetterButtonClicked(object sender, EventArgs e)
        {
            if (sender is Button btn)
            {
                _currentAttempt += btn.Text;
                btn.IsEnabled = false;

                CurrentAttemptLabel.Text = $"Your Attempt: {_currentAttempt}";

                if (_currentAttempt.Length == _currentWord.Length)
                {
                    CheckAttempt();
                }
            }
        }


        private async void CheckAttempt()
        {
            if (_currentAttempt.Equals(_currentWord, StringComparison.OrdinalIgnoreCase))
            {
                _score += 1 + (_streak * 2);
                _streak++;
                _correctWordsOnCurrentLevel++;

                await DisplayAlert("SUCCESS", "Correct!", "OK");

                // LEVEL UP CHECK
                if (_correctWordsOnCurrentLevel >= 25 && _currentLevelIndex < _levels.Count - 1)
                {
                    _currentLevelIndex++;
                    _correctWordsOnCurrentLevel = 0;
                    DifficultyPicker.SelectedIndex = _currentLevelIndex;

                    await DisplayAlert("LEVEL UP!", $"Next Level: {_levels[_currentLevelIndex]}", "OK");
                }

                // Reset attempt & letters
                _currentAttempt = "";
                CurrentAttemptLabel.Text = "";
                LetterButtonsLayout.Children.Clear();
            }
            else
            {
                _streak = 0;
                await DisplayAlert("INCORRECT", $"The word was: {_currentWord}", "OK");

                // Reset attempt so they can retry
                _currentAttempt = "";
                CurrentAttemptLabel.Text = "";
            }


            SaveState();
            UpdateDisplay();
        }


     

        private async void OnSkipWordClicked(object sender, EventArgs e)
        {
            const int skipCost = 10;

            if (_score < skipCost)
            {
                await DisplayAlert("SKIP BLOCKED", "You need at least 10 points to skip this word!", "OK");
                return;
            }

            _score -= skipCost;  // Deduct points
            _streak = 0;         // Reset streak
            _currentAttempt = string.Empty;

            if (CurrentAttemptLabel != null)
                CurrentAttemptLabel.Text = string.Empty;

            await DisplayAlert("SKIPPED", $"You skipped: {_currentWord}\n(-10 points)", "OK");

            SaveState();
            UpdateDisplay();

            OnGenerateWordClicked(sender, EventArgs.Empty);  // Or call your proper generate method
        }



    }


